mapper.update:
UPDATE product
SET prod_name = #{prodName}, prod_detail = #{prodDetail:VARCHAR}
	, manufacture_day = #{manuDate:VARCHAR}
	, price = #{price:NUMERIC}, image_file = #{fileName:VARCHAR}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.prod_no, p.prod_name, p.prod_detail, p.manufacture_day , p.price, p.image_file, p.reg_date ,CASE WHEN t.tran_status_code ='0' THEN '구매완료' WHEN t.tran_status_code ='1' THEN '배송중' WHEN t.tran_status_code ='2' THEN '배송완료' ELSE '판매중' END AS tran_status_code FROM product p, transaction t WHERE p.prod_no = t.prod_no(+)
AND p.prod_no = #{search.searchKeyword}
AND p.prod_name LIKE '%'||#{search.searchKeyword}||'%'
AND p.price LIKE CONCAT'%'||#{search.searchKeyword}||'%'
AND p.price BETWEEN #{search.searchValueHigh} AND #{search.searchValueLow}
AND p.price BETWEEN #{search.searchValueLow} AND #{search.searchValueHigh}
AND t.tran_status_code='0'
ORDER BY p.price ORDER BY p.prod_name
DESC
ORDER BY p.prod_no DESC
) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM( SELECT p.prod_no FROM product p, transaction t WHERE p.prod_no = t.prod_no(+)
AND p.prod_no = #{search.searchKeyword}
AND p.prod_name LIKE '%'||#{search.searchKeyword}||'%'
AND p.price LIKE CONCAT'%'||#{search.searchKeyword}||'%'
AND p.price BETWEEN #{search.searchValueHigh} AND #{search.searchValueLow}
AND p.price BETWEEN #{search.searchValueLow} AND #{search.searchValueHigh}
AND t.tran_status_code='0'
ORDER BY p.price ORDER BY p.prod_name
DESC
ORDER BY p.prod_no DESC
) countTable
